// Convenience exports for common token categories
export const colors: ColorTokens = tokens.colors || {} as ColorTokens;
export const spacing: SpacingTokens = tokens.spacing || {} as SpacingTokens;
export const typography: TypographyTokens = tokens.typography || {} as TypographyTokens;
export const components: ComponentTokens = tokens.components || {} as ComponentTokens;

// Type-safe token helpers
export const getColor = (path: string): string | undefined => {
  const keys = path.split('.');
  let value: any = colors;
  for (const key of keys) {
    value = value?.[key];
    if (value === undefined) return undefined;
  }
  return value;
};

export const getSpacing = (key: string): string | undefined => {
  return (spacing as any)?.spacing?.[key] || (spacing as any)?.[key];
};

export const getFontSize = (key: string): string | undefined => {
  return typography?.fontSize?.[key];
};

export const getFontWeight = (key: string): string | undefined => {
  return typography?.fontWeight?.[key];
};

// Component token helpers
export const getComponentToken = (component: string, property: string): any => {
  return (components as any)?.components?.[component]?.[property];
};

export const getComponentVariant = (component: string, variant: string, property: string): any => {
  return (components as any)?.variants?.[component]?.[variant]?.[property];
};

// CSS custom property helpers
export const toCSSVar = (tokenPath: string): string => {
  return `var(--${tokenPath.replace(/\./g, '-')})`;
};

export const getCSSVar = (category: string, ...keys: string[]): string => {
  const path = [category, ...keys].join('-');
  return `var(--${path})`;
};

export default tokens;
